import styled from 'styled-components'

export const StyledScoreBoard = styled.div`
    display: flex;
    justify-content: center;
    font-size: 3vh;

    `;

type Props = {
    side: string
}

export const StyledScore = styled.div<Props>`
    flex-grow: 1;
    flex-basis: 0;
    padding: 0 2vh;
    margin: 1vh 0;
    opacity: ;
    text-align: ${(Props) => Props.side === "left" ? "right" : "left"};
`;
