import styled from 'styled-components'
import { PADDLE_WIDTH, PADDLE_HEIGHT, PADDLE_POSITION } from '../../gameHelper';

type Props = {
    type: string;
    position: number;
}

const StyledPaddle = styled.div<Props>`
    width: ${PADDLE_WIDTH}vh;
    height: ${PADDLE_HEIGHT}vh;
    background-color: white;
    position: relative;
    top: ${(Props) => Props.position}vh;
    left: ${(Props) => Props.type === "left" ? 10 : G}px;
`;

export default StyledPaddle;
