import styled from 'styled-components'
import { PADDLE_WIDTH, PADDLE_HEIGHT } from '../../gameHelper';

type Props = {
    type: string;
}

const StyledPaddle = styled.div<Props>`
    width: ${PADDLE_WIDTH}vh;
    height: ${PADDLE_HEIGHT}vh;
    background-color: white;
    position: relative;
    top: 50vh;
    left: ${(Props) => Props.type === "left" ? 1 : 0}vw;
    right: ${(Props) => Props.type === "right" ? 100 : 0}vw;
`;

export default StyledPaddle;
